import React, { useState, useEffect } from "react";
import "../App.css";
import { Card, Icon, Modal } from "antd";
import Nav from "./Nav";
import { useParams } from "react-router-dom";

const { Meta } = Card;

function ScreenArticlesBySource() {
  const [articleBySourceList, setSarticleBySourceList] = useState([]);
  const [isModalVisible, setIsModalVisible] = useState(false);
  var { id } = useParams();

  useEffect(() => {
    async function loadArticlesBySource() {
      var rawResponse = await fetch(
        `https://newsapi.org/v2/top-headlines?sources=${id}&apiKey=e1af60fe24304a10815b363c551f94ea`
      );
      var response = await rawResponse.json();
      setSarticleBySourceList(response.articles);
    }
    loadArticlesBySource();
  });

  const showModal = () => {
    setIsModalVisible(true);
  };

  const handleOk = () => {
    setIsModalVisible(false);
  };

  const handleCancel = () => {
    setIsModalVisible(false);
  };

  return (
    <div>
      <Nav />

      <div className="Banner" />

      <div className="Card">
        {articleBySourceList.map((articleBySource) => (
          <div style={{ display: "flex", justifyContent: "center" }}>
            <Card
              style={{
                width: 300,
                margin: "15px",
                display: "flex",
                flexDirection: "column",
                justifyContent: "space-between",
              }}
              cover={<img alt="example" src={articleBySource.urlToImage} />}
              actions={[
                <Icon type="read" key="ellipsis2" onClick={showModal}/>,
                <Icon type="like" key="ellipsis" />,
              ]}
            >
              <Meta
                title={articleBySource.title}
                description={articleBySource.description}
              />
            </Card>
            <Modal
              title={articleBySourceList.title}
              visible={isModalVisible}
              onOk={handleOk}
              onCancel={handleCancel}
            >
              <p>{articleBySource.description}</p>
            </Modal>
          </div>
        ))}
        ;
      </div>
    </div>
  );
}

export default ScreenArticlesBySource;
