import React, { useState, useEffect } from "react";
import "../App.css";
import { Card, Icon } from "antd";
import Nav from "./Nav";

const { Meta } = Card;

function ScreenArticlesBySource() {

const [articleBySourceList, setSarticleBySourceList] = useState([]); 

  useEffect(() => {
    async function loadSource() {
      var rawResponse = await fetch("https://newsapi.org/v2/top-headlines?sources=google-news-fr&apiKey=e1af60fe24304a10815b363c551f94ea");
      var response = await rawResponse.json();

      var articlesBySourceFromAPI = response.sources.map((articlesourceAPI) => {
        return { source: sourceAPI.name, description: sourceAPI.description, id: sourceAPI.id };
      });
      setSarticleBySourceList(articlesBySourceFromAPI);
    }
    loadSource();
  }, []);


  return (
    <div>
      <Nav />

      <div className="Banner" />

      <div className="Card">
        <div style={{ display: "flex", justifyContent: "center" }}>
          <Card
            style={{
              width: 300,
              margin: "15px",
              display: "flex",
              flexDirection: "column",
              justifyContent: "space-between",
            }}
            cover={<img alt="example" src="../images/alaska.jpg" />}
            actions={[
              <Icon type="read" key="ellipsis2" />,
              <Icon type="like" key="ellipsis" />,
            ]}
          >
            <Meta title="Article title" description="Article Description" />
          </Card>
        </div>
      </div>
    </div>
  );
}

export default ScreenArticlesBySource;
