import React, { useState, useEffect } from "react";
import "../App.css";
import { Card, Icon } from "antd";
import Nav from "./Nav";
import { useParams } from "react-router-dom";

const { Meta } = Card;

function ScreenArticlesBySource() {

const [articleBySourceList, setSarticleBySourceList] = useState([]); 

var { id } = useParams();


  useEffect(() => {
    async function loadArticlesBySource() {
      var rawResponse = await fetch(`https://newsapi.org/v2/top-headlines?sources=${id}&apiKey=e1af60fe24304a10815b363c551f94ea`);
      var response = await rawResponse.json();

      var articlesBySourceFromAPI = response.articles.map((articleBySourceAPI) => {
        return { titre: articleBySourceAPI.title, description: articleBySourceAPI.description, img: articleBySourceAPI.urlToImage };
      });
      setSarticleBySourceList(articlesBySourceFromAPI);
      // console.log(articlesBySourceFromAPI);
    }
    loadArticlesBySource();
  }, );

// console.log(articleBySourceList);
  return (
    <div>
      <Nav />

      <div className="Banner" />

      <div className="Card">
        <div style={{ display: "flex", justifyContent: "center" }}>
          <Card
            style={{
              width: 300,
              margin: "15px",
              display: "flex",
              flexDirection: "column",
              justifyContent: "space-between",
            }}
            cover={<img alt="example" src={articleBySourceList.img} />}
            actions={[
              <Icon type="read" key="ellipsis2" />,
              <Icon type="like" key="ellipsis" />,
            ]}
          >
            <Meta title={articleBySourceList.titre} description={articleBySourceList.titre} />
          </Card>
        </div>
      </div>
    </div>
  );
}

export default ScreenArticlesBySource;
