import React, { useState, useEffect } from "react";
import "../App.css";
import { List, Avatar } from "antd";
import Nav from "./Nav";

function ScreenSource() {

  const [sourceList, setSourceList] = useState([]);
  const [descList, setDescList] = useState([]);

  useEffect(() => {
    async function loadSource() {
      var rawResponse = await fetch("https://newsapi.org/v2/top-headlines?apiKey=e1af60fe24304a10815b363c551f94ea&country=be&language=fr");
      var response = await rawResponse.json();
      // setSourceList(response);

      var sourcesFromAPI = response.articles.map((sourceAPI) => {
        return { source: sourceAPI.source.name };
      });
      setSourceList(sourcesFromAPI);
      console.log(sourcesFromAPI);


      var descFromAPI = response.articles.map((descAPI) => {
        return { description: descAPI.description };
      });
      setDescList(descFromAPI);
      console.log(descFromAPI);
    }
    loadSource();
  }, []);



  return (
    <div>
      <Nav />

      <div className="Banner" />

      <div className="HomeThemes">
        <List
          itemLayout="horizontal"
          dataSource={sourceList}
          renderItem={(item) => (
            <List.Item>
              <List.Item.Meta
                avatar={
                  <Avatar src="https://zos.alipayobjects.com/rmsportal/ODTLcjxAfvqbxHnVXCYX.png" />
                }
                title={item.source}
                description="Ant Design, a design language for background applications, is refined by Ant UED Team"
              />
            </List.Item>
          )}
        />
      </div>
    </div>
  );
}

export default ScreenSource;
