import React, { useState, useEffect } from "react";
import "../App.css";
import { List, Avatar } from "antd";
import Nav from "./Nav";

function ScreenSource() {

  const [sourceList, setSourceList] = useState([]);

  useEffect(() => {
    async function loadMovie() {
      var rawResponse = await fetch("https://newsapi.org/v2/top-headlines?apiKey=e1af60fe24304a10815b363c551f94ea&country=be&language=fr");
      var response = await rawResponse.json();
      setMovieList(response);

      var rawResponse2 = await fetch("/wishlist-movie");
      var response2 = await rawResponse2.json();
      var wishListFromDB = response2.allMovies.map((movie) => {
        return { name: movie.name, img: movie.img };
      });
      setMoviesWishList(wishListFromDB);
      setMoviesCount(response2.allMovies.length);
    }
    loadMovie();
  }, []);

  const data = [
    {
      title: "Ant Design Title 1",
    },
    {
      title: "Ant Design Title 2",
    },
    {
      title: "Ant Design Title 3",
    },
    {
      title: "Ant Design Title 4",
    },
  ];

  return (
    <div>
      <Nav />

      <div className="Banner" />

      <div className="HomeThemes">
        <List
          itemLayout="horizontal"
          dataSource={data}
          renderItem={(item) => (
            <List.Item>
              <List.Item.Meta
                avatar={
                  <Avatar src="https://zos.alipayobjects.com/rmsportal/ODTLcjxAfvqbxHnVXCYX.png" />
                }
                title={<a href="https://ant.design">{item.title}</a>}
                description="Ant Design, a design language for background applications, is refined by Ant UED Team"
              />
            </List.Item>
          )}
        />
      </div>
    </div>
  );
}

export default ScreenSource;
